from Crypto.Util.number import *

flag = b'SharkCTF{xxxxxxxxxxxx}'
m = bytes_to_long(flag)

p, q = getPrime(512), getPrime(512)
n = p * q
assert n > m
g = n + 1
hint = pow(g, p, n**2)
e = 0x10001
c = pow(m, e, n**2) 
print(f"n = {n}\nc = {c}\nhint = {hint}")
